def testModelo(alt):
    global cont 
    cont += 1
    print('Numero de ciclos: ', cont)
    for a in alt[:1]:
        fitness_final, numero_de_palavras_certas, numero_palavra_erradas, palavras_certas = a.scoreFitness
        print(round(fitness_final,2), ' --  ', numero_de_palavras_certas)
        print(palavras_certas)
        print(a.chave)
        print(a.novasPalavras)
        print('###########')
        # input('Aperte qualquer coisa para continuar')
    print('')